INSERT INTO Comment VALUEs('272', 'really time consuming, but get to work on fun projects, through which to better understand oo design (be prepared to pull all-nighters) ' );
INSERT INTO Comment VALUEs('273', 'Prepare 20+ hours per week if you want to carry your team. Or be carried through the hardcore class if you have god like teammates. ' );
