INSERT INTO Comment VALUEs('1288', 'Unstructured lectures, but exams were reasonable and grading wasn''t bad. Interesting materials. ' );
INSERT INTO Comment VALUEs('1289', 'fun and easy class ' );
INSERT INTO Comment VALUEs('1290', 'Fantastic professor with very engaging lecture ' );
INSERT INTO Comment VALUEs('1291', 'Very interesting content and light workload ' );
INSERT INTO Comment VALUEs('1291', 'Very interesting and easy material, a lot of reading which is required because there is a chapter quiz online almost every week. Professor is super nice and approachable. ' );
INSERT INTO Comment VALUEs('1292', 'I LOVE IT. 8:30 twice a week, but worth it. ' );
INSERT INTO Comment VALUEs('1293', 'many short assignments and two major papers. Also midterms and final. Pop quizes ' );
