INSERT INTO Comment VALUEs('1496', 'Light workload, easy course, the prof is super chill (he is retiring soon) ' );
INSERT INTO Comment VALUEs('1496', 'Moderate workload, managable exams, but lectures can sometimes be dull. Berger is a knowledgeable professor who is highly respected in the academia and in the industry. ' );
INSERT INTO Comment VALUEs('1497', 'The material is very hard. Homework and lab are very time consuming. pre-reqs are stats 250, 210. ' );
INSERT INTO Comment VALUEs('1498', 'Not too much workload. Interesting course material ' );
INSERT INTO Comment VALUEs('1499', 'Do not take it unless you are a stats major. If unfortunately you have to take it, do not take it with Steorts. The lecture is boring and she can''t explain things well. Unreasonably harsh. Also only gives out A/B/C/D grades(no A-/B+/B-/C+) ' );
INSERT INTO Comment VALUEs('1500', 'Beka makes the course much easier than it should be ' );
