INSERT INTO Comment VALUEs('1500', 'The workload is ok and the professor is really a nice person. However, she''s not good at teaching. Labs are very helpful. ' );
INSERT INTO Comment VALUEs('1500', 'Requirement for stats major, awful professor however. Do avoid. ' );
INSERT INTO Comment VALUEs('1500', 'Content is hard but the exam is easy; make sure you take the course with Rebecca ' );
